function init() {
  function colorCountry(cases) {
    if (cases < 50) {
      return "#fea0a0"
    }
    else if (cases < 100) {
      return "#e38080"
    }
    else if (cases < 500) {
      return "#c86060"
    }
    else if (cases < 1000) {
      return "#ad3f3f"
    }
    else if (cases < 5000) {
      return "#921f1f"
    }
    else if (cases > 5000) {
      return "#770000"
    }
  }


  L.geoJson(countries, {
    style: function(feature) {
      console.log(feature.properties.cases);
      return {
        fillColor: colorCountry(feature.properties.cases),
        color: "white",
        fillOpacity: 0.8,
        weight: 1.5
      };
    }
  }).addTo(map);


}

init();

d3.selectAll("#selectMap").on("change", displayMap);

function colorPoverty(pct) {
  if (pct < 1) {
    return "#fea0a0"
  }
  else if (pct < 5) {
    return "#e38080"
  }
  else if (pct < 10) {
    return "#c86060"
  }
  else if (pct < 25) {
    return "#ad3f3f"
  }
  else if (pct < 50) {
    return "#921f1f"
  }
  else if (pct > 50) {
    return "#770000"
  }
}

function displayMap() {
  var selection = d3.select("#selectMap").property("value");
  console.log(selection)
  
  if (selection === "Human Trafficking") {
    init()    
  }

  else if (selection === "Poverty <$1.90/day") {

    L.geoJson(countries, {
      style: function(feature) {
        return {
          fillColor: colorPoverty(feature.properties.poverty_group_1),
          
          // color: colorLines(),
          fillOpacity: 0.8,
          weight: 1.5,
          color: "white"
        };
      }
    }).addTo(map);
  }

  else if (selection === "Poverty <$3.20/day") {
    L.geoJson(countries, {
      style: function(feature) {
        return {
          fillColor: colorPoverty(feature.properties.poverty_group_2),
          
          // color: colorLines(),
          fillOpacity: 0.8,
          weight: 1.5,
          color: "white"
        };
      }
    }).addTo(map);
  }

  else if (selection === "Poverty <$5.50/day") {
      L.geoJson(countries, {
      style: function(feature) {
        return {
          fillColor: colorPoverty(feature.properties.poverty_group_3),
          
          // color: colorLines(),
          fillOpacity: 0.8,
          weight: 1.5,
          color: "light green"
        };
      }
    }).addTo(map);
  };

};

d3.json("Output/countries.json").then(coordinate_pairs => {

  console.log(coordinate_pairs);
  L.mapbox.accessToken = 'pk.eyJ1Ijoic3lhbGxhcCIsImEiOiJjazd3aGxnc3MwMmg5M2xxZ3h0Y3puMnN0In0.1ltissQsYvEY21zrOQ8Cug';


  // Displays worldwide view upon page loading (setView arranges according to Lat, Lng, zoom level)
  //Default zoom level is 2 to refelct worldwide flight paths, however, for this project, it is set
  //at 3 to focus on high traffic areas in South America, Eastern Europe, Western Africa, 
  //South East Asia, and Oceania (Australia/NZ/South Pacific)
  //Setting zoom to 2 enables user to see worldwide view of paths, but does not focus on high
  //traffic areas. 
  map2 = L.mapbox.map('map2')
    .setView([25, 0], 2)
    //1. Predefined style from Mapbox: Satellite terrain view without borders/country labels
    //.addLayer(L.mapbox.styleLayer('mapbox://styles/mapbox/satellite-v9'));
    
    //2. "Frank" style: Grey map view with borders, country labels, and oceans
    //Loaded to Sree's account name
    //.addLayer(L.mapbox.styleLayer('mapbox://styles/syallap/ck8hw9maq06e41ip5d8z0ixd3'));

    //3. SELECTED LAYER: "Decimal" style: dark view with teal/aquamarine labels/borders 
    //Loaded to Sree's account name
    .addLayer(L.mapbox.styleLayer('mapbox://styles/syallap/ck8hw74iw06a41ipc5ez1ewok'));
    
  // "Disable drag and zoom handlers. Making this effect work with zooming and panning
  // would require a different technique with different tradeoffs." - from arc.js site

  //For our project, dragging has been enabled in order for the user to move to other regions.
  //However, zooming in presents lots of unorganized lines. 
  //Recommendation: wait until all paths have loaded, so that it is easier to study each region
  map2.dragging.enable();
  map2.touchZoom.disable();
  map2.doubleClickZoom.disable();
  map2.scrollWheelZoom.disable();
  if (map2.tap) map2.tap.disable();
  
  // Transforms the [lat,lng] format into {x, y} format as expected by arc.js.
  function obj(ll) { return { y: ll[0], x: ll[1] }; }
  
  for (var i = 0; i < coordinate_pairs.length; i++) {
    // Transform each pair of coordinates into great circle paths
    var generator = new arc.GreatCircle(
      obj(coordinate_pairs[i][0]),
      obj(coordinate_pairs[i][1]));
    var line = generator.Arc(100, { offset: 10 });
    // "Leaflet expects [lat,lng] arrays, but a lot of software does the opposite, 
    //including arc.js, so we flip here." - from site
    var newLine = L.polyline(line.geometries[0].coords.map(function (c) {
      return c.reverse();
    }), {
      //color: "#DF2F20", //Pairs with Mapbox style 2 (red flight lines)
      color: "#28C599",
      weight: 1,
      opacity: 0.5
    })
      .addTo(map2);
    var totalLength = newLine._path.getTotalLength();
    newLine._path.classList.add('path-start');
    // This pair of CSS properties hides the line initially
    // (http://css-tricks.com/svg-line-animation-works/) - really helpful link 
    //included with original url from line 1

    newLine._path.style.strokeDashoffset = totalLength;
    newLine._path.style.strokeDasharray = totalLength;
    //"Offset timeout here: setTimeout makes a function run after a [few] milliseconds"
    setTimeout((function (path) {
      return function () {
        // "setting the strokeDashoffset to 0 triggers the animation"
        path.style.strokeDashoffset = 0;
      };
    })(newLine._path), i * 100);
  }
})
